<style>

	.animations {
		display: none;
		margin: 5px;
		display: flex;
		justify-content: center;
	}

	div > svg {
		/* position: absolute; */
		/* visibility: hidden; */
		width: 200px;
		height: 200px;
		padding: 20px;
		stroke-dashArray: 100%;
		stroke-dashoffset: 100%;
		
		animation-name: DrawLine, FadeStroke, FillIn, CoolOut;
		/* animation-name: DrawLine, FadeStroke, FillIn, FlickerOut; */
		animation-duration: 1s, .3s, .8s, .5s;
		animation-delay: 0s, .8s, .3s, 3.5s;
		
		animation-timing-function: ease-in;
		animation-timing-function: linear; /* Update to linear */
		animation-fill-mode: both;
		animation-direction: alternate;
		animation-iteration-count: 1;
	}

	@keyframes DrawLine {
		from {
			stroke-opacity: 0;
			stroke-dashOffset: 100%; 
			stroke-width: 10px
		}
		to { 
			stroke-opacity: 1; 
			stroke-dashOffset: 0%; 
			stroke-width: 20px
		}
	}

	@keyframes FadeStroke {
		to { 
			stroke-opacity: 0; 
			stroke-width: 0;
		}
	}

	@keyframes FillIn {
		from { fill-opacity: 0; }
		to { fill-opacity: 1; }
	}
	
	@keyframes CoolOut {
		20% {
			fill-opacity: 1;
			stroke-opacity: 1; 
			stroke-dashOffset: 0%; 
			stroke-width: 20px
		}
		100% { 
			fill-opacity: 0;
			stroke-opacity: 0;
			stroke-dashOffset: 100%; 
			stroke-width: 10px;
			stroke-width: 0;
		}
	}
	
	@keyframes FlickerOut {
		0% { opacity: 1; }
		5% { opacity: 0; }
		10% { opacity: 1; }
		15% { opacity: 0; }
		20% { opacity: 1; }
		25% { opacity: 0; }
		30% { opacity: 1; }
		35% { opacity: 0; }
		40% { opacity: 1; }
		45% { opacity: 0; }
		50% { opacity: 1; }
		55% { opacity: 0; }
		60% { opacity: 1; }
		65% { opacity: 0; }
		70% { opacity: 1; }
		75% { opacity: 0; }
		78% { opacity: 1; }
		81% { opacity: 0; }
		84% { opacity: 1; }
		/* 87% { opacity: 0; } */
		/* 90% { opacity: 1; } */
		/* 93% { opacity: 0; } */
		/* 96% { opacity: 1; } */
		/* 98% { opacity: 0; } */
		/* 99% { opacity: 1; } */
		100% { opacity: 0; }
	}


	*.background {
		stroke: white;
		fill: white;
	}
	
	*.background-accent {
		stroke: #E6E6E6;
		fill: #E6E6E6;
	}
	
	.full *.primary {
		stroke: teal;
		fill: teal;
	}
	
	.full *.accent {
		stroke: coral;
		fill: coral;
	}
	
	.web *.primary {
		stroke: navy;
		fill: navy;
	}
	
	.web *.accent {
		stroke: goldenrod;
		fill: goldenrod;
	}

	.application *.primary {
		stroke: orangered;
		fill: orangered;
	}
	
	.application *.accent {
		stroke: aqua;
		fill: aqua;
	}
	
	.game *.primary {
		stroke: purple;
		fill: purple;
	}
	
	.game *.accent {
		stroke: forestgreen;
		fill: forestgreen;
	}
	
	.ai *.primary {
		stroke: darkolivegreen;
		fill: darkolivegreen;
	}
	
	.ai *.accent {
		stroke: dodgerblue;
		fill: dodgerblue;
	}
	
	.aspiring *.primary {
		stroke: magenta;
		fill: magenta;
	}
	
	.aspiring *.accent {
		stroke: skyblue;
		fill: skyblue;
	}

</style>
